mapper.select:
select member_id, name, token
from members
where member_id = #{id} and password = #{password}

select member_id, name, birthday, gender, image, member_type, role, token
from members
where member_id = #{userId}

mapper.update:
update members
set token = #{token}
where member_id = #{id}

mapper.select:
select token
from members
where member_id = #{id}

mapper.update:
update members
set token = #{token, jdbcType=VARCHAR}
where member_id = #{id}

mapper.select:
select member_id, password, name, birthday, gender, member_type, role, token
from members
where member_id = #{email}

select member_id, password, name, birthday, gender, member_type, role, token
from members
where user_name = #{name}

mapper.update:
update members
birthday = #{birthday}, gender = #{gender}, image = #{image}, member_type = #{memberType}, role = #{role},
where member_id = #{id}

mapper.select:
select count(*)
from members
where member_id = #{id}
